import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import styled from "styled-components";

const Carousel = () => {
  // 옵션
  const settings = {
    dots: true,
    infinite: true,
    speed: 10,
  };

  return (
    <Banner
      style={{
        backgroundImage: "url(/images/event.png",
        backgroundSize: "100%",
      }}
    >
      <div className="carousel">
        <Slider {...settings}>
          <div>Slide 1</div>
          <div>Slide 2</div>
          <div>Slide 3</div>
        </Slider>
      </div>
    </Banner>
  );
};

export default Carousel;

const Banner = styled.div`
  position: relative;
  width: 100%;
  height: 500px;
  background: #f2f2f2;
  margin-bottom: 80px;

  .right {
    position: absolute;
    transform: translateY(-50%);
    top: 50%;
    right: 5%;
    background: transparent;
  }

  .right img {
    background: transparent;
  }

  .left {
    position: absolute;
    top: 50%;
    left: 5%;
    transform: translateY(-50%);
    background: transparent;
  }

  .left img {
    background: transparent;
  }
`;
