import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import { getProducts } from "../../fetch/fetch";

export const Detail = ({ convertPrice, cart, setCart }) => {
  const { id } = useParams();

  const [product, setProduct] = useState({});
  const [count, setCount] = useState(1);

  const handleCountPlus = (type) => {
    setCount(count + 1);
  };

  const handleCountMinus = (type) => {
    if (count === 0) {
      return;
    }
    setCount(count - 1);
  };

  // 중복된 물건
  const overlapQuantity = (id, quantity) => {
    const found = cart.filter((el) => el.id === id)[0];
    const idx = cart.indexOf(found);
    const cartItem = {
      id: product.id,
      image: product.image,
      name: product.name,
      price: product.price,
      provider: product.provider,
      quantity: quantity,
    };
    setCart([...cart.slice(0, idx), cartItem, ...cart.slice(dix + 1)]);
  };

  //장바구니
  const handleCart = () => {
    const cartItem = {
      id: product.id,
      image: product.image,
      name: product.name,
      price: product.price,
      provider: product.provider,
      quantity: count,
    };
    const found = cart.find((el) => el.id === cartItem.id);
    if (found) overlapQuantity;
    else {
    }
    setCart([...cart, cartItem]);
  };

  // console.log("cart:", cart);

  useEffect(() => {
    getProducts().then((data) => {
      setProduct(
        data.data.products.find((product) => product.id === parseInt(id))
      );
    });
  }, [id]);

  return (
    product && (
      <>
        <Main>
          <section className="product">
            <div className="product_img">
              <img src={product.image} alt="product" />
            </div>
          </section>
          <section className="product">
            <div className="product_info">
              <p className="seller_store">{product.provider}</p>
              <p className="product_name">{product.name}</p>
              <span className="price">
                {convertPrice(product.price + "")}
                <span className="unit">원</span>
              </span>
            </div>

            <div className="delivery">
              <p>택배배송 / 무료배송</p>
            </div>

            <div className="line"></div>

            <div className="amount">
              <img
                className="minus"
                src="/images/icon-minus-line.svg"
                alt="minus"
                onClick={handleCountMinus}
              />

              <div className="count">
                <span>{count}</span>
              </div>

              <img
                className="plus"
                src="/images/icon-plus-line.svg"
                alt="plus"
                onClick={handleCountPlus}
              />
            </div>

            <div className="line"></div>

            <div className="sum">
              <div>
                <span className="sum_price">총 상품 금액</span>
              </div>

              <div className="total_info">
                <span className="total">
                  총 수량 <span className="total_count">{count}개</span>
                </span>
                <span className="total_price">
                  {convertPrice(product.price * count)}
                  <span className="total_unit">원</span>
                </span>
              </div>
            </div>

            <div className="btn">
              <button className="btn_buy">바로 구매</button>
              <button className="btn_cart" onClick={handleCart}>
                장바구니
              </button>
            </div>
          </section>
        </Main>
      </>
    )
  );
};

