import { Link } from "react-router-dom";

import styled from "styled-components";

export const Product = ({ product, convertPrice }) => {
  return (
    <Products>
      <Link to="#">
        <div className="product--image">
          <img src={product.image} alt="product" />
        </div>
      </Link>
      <div className="store">
        <span>{product.provider}</span>
      </div>

      <div className="product--name">
        <span>{product.name}</span>
      </div>

      <div className="product--price">
        <span className="price">{product.price}</span>
        <span className="unit">Ïõê</span>
      </div>
    </Products>
  );
};

const Products = styled.div`
  display: flex;
  flex-direction: column;

  .product--image {
    width: 380px;
    height: 380px;
    border-radius: 10px;
    margin-bottom: 16px;
  }
  .product--image img {
    width: 100%;
    height: 100%;
    border-radius: 10px;
    border: 1px solid #c4c4c4;
  }

  .store {
    margin-bottom: 10px;
  }
  .store span {
    font-size: 16px;
    line-height: 22px;
    color: #767676;
  }
  .product--name {
    margin-bottom: 10px;
  }
  .product--name span {
    font-size: 18px;
    line-height: 22px;
    color: #000000;
  }
  .price {
    font-size: 24px;
    font-weight: bold;
    line-height: 30px;
    color: #000000;
  }
  .unit {
    font-size: 16px;
    line-height: 20px;
    color: #000000;
  }

  @media (max-width: 968px) {
    .product--image {
      width: 220px;
      height: 220px;
    }
    .product--name span {
      font-size: 14px;
    }
  }

  @media (max-width: 768px) {
    .product--image {
      width: 300px;
      height: 300px;
    }
  }

  @media (max-width: 360px) {
    .product--image {
      width: 200px;
      height: 200px;
    }

    .store span {
      font-size: 12px;
      line-height: 22px;
      color: #767676;
    }

    .product--name span {
      font-size: 12px;
    }
  }
`;
