import styles from "./topNavigationBar.module.css";
import { Link } from "react-router-dom";
import styled from "styled-components";

export const TopNavigationBar = () => {
  return (
    <Header>
      <div className="inner">
        <Link to="/">
          <h1 className="logo">
            <img
              src="https://logos.textgiraffe.com/logos/logo-name/Han-designstyle-jungle-m.png"
              alt="logo"
            />
          </h1>
        </Link>
        <div className="input_wrap">
          <input type="text" placeholder="상품을 검색해보세요!" />
          <img src="/images/icon-search.svg" alt="search" />
        </div>
      </div>

      <div className={styles.menu}>
        <Link to="/cart">
          <div className={styles.shopping_cart}>
            <img src="/images/icon-shopping-cart.svg" alt="cart" />
            <span>장바구니</span>
          </div>
        </Link>
        <Link to="">
          <div className={styles.mypage}>
            <img src="/images/icon-user.svg" alt="user" />
            <span>로그인</span>
          </div>
        </Link>
      </div>
    </Header>
  );
};

const Header = styled.div`
  width: 100%;
  height: 90px;
  display: flex;
  justify-content: space-around;
  align-items: center;
  box-shadow: 0px 4px 5px rgba(0, 0, 0, 0.1);

  .inner {
    display: flex;
    align-items: center;
  }
  .inner .logo {
    width: 124px;
    height: 38px;
    margin-right: 30px;
  }

  .inner .logo img {
    width: 124px;
    height: 38px;
  }
  .input_wrap {
    position: relative;
    width: 400px;
    height: 46px;

    input {
      width: 400px;
      height: 46px;
      border: 2px solid #19ce60;
      border-radius: 50px;
      padding: 13px 22px;
      font-size: 16px;
      line-height: 20px;
      outline: none;
    }
    img {
      position: absolute;
      top: 50%;
      right: 15px;
      transform: translateY(-50%);
      width: 19px;
      height: 19px;
    }
  }
`;
